var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Assign = require("./Assign1");

test("basic", function(){
	var assign = Assign();
	assert(assign instanceof Assign);
	assert(assign.constructor === Assign);
});

test("usage", function(){
	var obj = {
		assign: Assign().fn
	};

	obj.assign("one", 1);

	assert(obj.one === 1);

	obj.assign({
		two: true,
		three: "four"
	}, {
		five: 6
	});

	assert(obj.two === true);
	assert(obj.three === "four");
	assert(obj.five === 6);


});