var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Assign = require("./Assign2");

test("basic", function(){
	var assign = Assign();
	assert(assign instanceof Assign);
	assert(assign.constructor === Assign);
});

test("usage", function(){
	var obj = {};

	Assign({ target: obj });

	obj.assign("one", 1);

	assert(obj.one === 1);

	obj.assign({
		two: true,
		three: "four"
	}, {
		five: 6
	});

	assert(obj.two === true);
	assert(obj.three === "four");
	assert(obj.five === 6);

	var obj2 = Object.create(obj);
	assert(obj2.hasOwnProperty("assign") === false);
	obj2.assign;
	assert(obj2.hasOwnProperty("assign") === true);
	assert(obj2.assign.mfn.target === obj2);
	assert(obj2.assign.mfn !== obj.assign.mfn);
});