var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var assign = require("./Assign2");

test("usage", function(){
	var obj = {
		assign: assign
	};

	obj.assign("one", 1);

	assert(obj.one === 1);

	obj.assign({
		two: true,
		three: "four"
	}, {
		five: 6
	});

	assert(obj.two === true);
	assert(obj.three === "four");
	assert(obj.five === 6);

	obj.assign({
		sub: {
			assignedTo: function(parent, name){
				this.parent = parent;
				this.name = name;
			}
		}
	});

	assert(obj.sub.parent === obj);
	assert(obj.sub.name === "sub");
});