var createConstructor = require("./createConstructor");
var extend = require("./extend");

var track = require("track42");


var Base1 = createConstructor("Base1");
track(Base1);
track(Base1.prototype);

Base1.assign({
	extend: extend,
	createConstructor: createConstructor
});

Base1.prototype.assign({
	instantiate: function(){
		this.assign.apply(this, arguments);
		this.init();
	},
	init: function(){}
});

// TODO: move this to a separate class
var Morphic = Base1.extend({
	name: "Morphic",
	morphic: true // the instantiate --> return should probably be isolated in a second class, so you don't have to remember to always return this.
});

Base1.x = function(){
	// autoInit ==> false
};

module.exports = Base1;