var createConstructor = module.exports = function(){
	var Constructor = function(o){
		if (!o || !o._$_contextualized)
			return new Constructor({
				ctx: this,
				_$_contextualized: true,
				args: arguments
			});

		this.ctx = o.ctx;
		this.instantiate.apply(this, o.args);
	};
	return Constructor;
};