var test = require("test42");
var View = require("view42/v8");
var util = require("util42");
var is = util.is;

var assert = test.assert;

var contextual = require("./contextual");
var Mod = require("mod42/v1");

var Contextual = contextual();

Contextual.prototype.instantiate = function(value){
	console.log("instantiate", this.ctx, arguments);
	this.ctx.value = value;
}

test("basic", function(){
	var mod = Mod({
		ctxl: Contextual
	});

	mod.ctxl("hello");

	assert(mod.value === "hello");
});