var createConstructor = function(){
	var Base = function(){
		this.instantiate.apply(this, arguments);
	};
	return Base;
};

var Base = createConstructor();
Base.createConstructor = createConstructor;

Base.extend = function(o){
	// createConstructor could be embedded here, but then we can't override it
	var Ext = this.createConstructor();
	Ext.createConstructor = this.createConstructor;
	Ext.extend = this.extend;

	// Setup inheritance
	Ext.prototype = Object.create(this.prototype);
	Ext.prototype.constructor = Ext;

	return Ext;
};

Base.prototype.instantiate = function(){};

module.exports = Base;