var Base = require("../v1");

var Base2 = module.exports = Base.extend({
	set: set,
	instantiate: function(){
		this.set.apply(this, arguments);
		this.initialize && this.initialize();
	},
	clone: function(){
		var clone = Object.create(this);
		clone.instantiate.apply(clone, arguments);
		return clone;
	}
});