var Mod1 = require("mod42/Mod1");
var copy = require("copy42")().fn;

var Coll1 = module.exports = Mod1.extend({
	name: "Coll1",
	values: [],
	instantiate: function(){
		this.inst_values();
		return Mod1.prototype.instantiate.apply(this, arguments);
	},
	inst_values: function(){
		this.values = copy(this.values);
	},
	append: function(){
		for (var i = 0; i < arguments.length; i++){
			this.values.push(arguments[i]);
		}
		return this;
	},
	each: function(fn){
		for (var i = 0; i < this.values.length; i++){
			fn.call(this, this.values[i], i);
		}
		return this;
	},
	add: function(){
		return this.append.apply(this, arguments);
	}
}).set({
	extend: {
		instantiatePrototype: function(Ext, Base){
			Ext.prototype.inst_values();
		},
	}
});