var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Coll1 = require("./Coll1");

test("basic", function(){
	var coll = Coll1();

	assert(coll instanceof Coll1);
	assert(coll.values !== Coll1.prototype.values);
});

test("append", function(){
	var coll = Coll1({
		append: [1, 2, 3]
	});

	var values = [];
	coll.each(function(v){
		values.push(v);
	});

	assert(values[0] === 1);
	assert(values[1] === 2);
	assert(values[2] === 3);
});

test("extend", function(){
	var Coll2 = Coll1.extend({
		append: [1, 2, 3]
	});

	assert(Coll2.prototype.values !== Coll1.prototype.values);

	var coll = Coll2();
	
	var values = [];
	coll.each(function(v){
		values.push(v);
	});

	assert(values[0] === 1);
	assert(values[1] === 2);
	assert(values[2] === 3);

});