var View = require("view42");
var $ = require("jquery");

require("less42");

module.exports = {
	Grid: View.extend({
		name: "Grid",
		behaviors: function(){
			
			$(window).resize(function(){
				if (this.$el.width() < this.fullAt){
					this.removeClass("two three");
				} else if (this.$el.width() < this.twoAt){
					this.removeClass("three").addClass("two")
				} else {
					this.addClass("three").removeClass("two");
				}
			}.bind(this));

		}
	}),
	Cols: View.extend({
		name: "Cols",
		left: View.x(),
		right: View.x(),
		behaviors: function(){
			$(window).resize(function(){
				if (this.$el.width() < this.break){
					this.addClass("break");
				} else {
					this.removeClass("break");
				}
			}.bind(this));
		},
		content: function(){
			this.left.render();
			this.right.render();
		}
	})
};


