var log = require("logger");

log("yo");

var wrap = log.wrapc("wrap", function(arg){
	log("this is inside a wrapped function, named 'wrap'!!");
});

wrap();
wrap("one");
wrap(true);

log.groupc("log.group() creates a group");
log("and then statements get grouped, per usual")
wrap(3);
log.end();


require("./another");