var Base = require("base42");
var is = require("util42").is;
var track = require("track42");

var Mfn = Base.extend({
	name: "Mfn",
	init: function(){
		var self = this;

		this.fn = function(){
			return self.main.apply(self, [this].concat([].slice.call(arguments)));
		};

		this.fn.mfn = this;

		this.fn.set = function(o){
			return this.mfn.clone.apply(this.mfn, arguments).fn;
		};
	},
	clone: function(){
		var clone = Object.create(this);
		track(clone);
		clone.instantiate.apply(clone, arguments);
		return clone;
	},
	main: function(ctx){}
});

module.exports = Mfn;