var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Mfn = require("./Mfn4");
var Base = require("base42/debug");

var Method = require("method42/v6")

test("assertClass", function(){
	test.assertClass(Mfn);
});

test("basic", function(){

	var mod = Base({
		mfn: Mfn({
			exec: function(ctx){
				console.log("mfn executed!");
				this.other(ctx);
			},
			other: function(ctx){
				assert(ctx === mod);
			}
		})
	});

	mod.mfn();
});