module.exports = {
	init_define: function(parent, name){
		var self = this;
		Object.defineProperty(parent, name, {
			get: function(){
				return self.getter(this);
			},
			set: function(value){
				self.setter(value, this);
			},
			configurable: true
		});
	},
	setter: function(){},
	getter: function(){}
};