var Base = require("base42");
var assign = require("assign42/v2");
var track = require("track42");

var Mod = module.exports = Base.extend({
	name: "Mod1",
	assign: assign,
	assignedTo: function(parent, name){
		if (this.adopt && !this.hasOwnProperty("parent")){
			this.parent = parent;
			this.name = name;
		}
	}
}).assign({
	sub: function(){
		var args = [].slice.call(arguments);
		return this.apply(null, [{ adopt: true }].concat(args));
	}
});