var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Mod = require("./Mod10");

test("basic", function(){
	var mod = Mod();
	assert(mod instanceof Mod);
	assert(mod.constructor === Mod);
	assert(mod.proto === Mod.prototype);
	mod.log("hello");
});

test("logging", function(){
	view("Visually inspect console for 1 'hello'");
	var mod = Mod({
		hello: function(){
			this.log("hello");
		}
	});

	mod.hello();
	mod.log = true;
	mod.hello();
	mod.log = false;
	mod.hello();
});

test("logging2", function(){
	view("Visually inspect for 1 hello.  Also, toggle log: true with comments in order to turn it on/off.");
	var mod = Mod({
		log: true, // toggle me with comments
		hello: function(){
			this.log("hello");
		}
	});

	mod.hello();
	mod.set({
		log: false
	});
	mod.hello();
});