var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Mod = require("./Mod3");

test("basic", function(){
	var mod = Mod();
	assert(mod instanceof Mod);
	assert(mod.constructor === Mod);

	assert(is.fn(mod.set));
});

test("assign", function(){
	var mod = Mod({
		myMethod: function(){
			console.log("this is myMethod", this, arguments);
			this.other();
		},
		other: function(){
			this.log("stuff");
		}
	});

	mod.log.render();
	mod.myMethod();

	window.mod = mod;
});