var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var Q1 = require("./Q1");

test("basic", function(){
	var q = Q1();

	assert(q instanceof Q1);
	assert(q.values !== Q1.prototype.values);

	var check = {};
	q.append(function(){
		q.one = 1;
	});

	q.exec();

	assert(q.one === 1);
});