var set = SetMfn({
	/*
	Mod({
		set: function(value){
			//...
		}
	})
	*/
	other: function(mod, arg){
		if (is.fn(arg)){
			mod.value = arg;
		} else {
			this.value.call(mod, arg);
		}
	}
}).fn;

SetMfn.set = set;
SetMfn.prototype.set = set;

/*****/


test("short form for set.value", function(){
	// this should be moved into another version
	var check = {};
	var obj = {
		set: Set().fn
	};
	
	obj.set({
		set: function(value){
			check.itWasCalled = true;
			assert(this === obj);
			assert(value === "yo");
		}
	});

	obj.set("yo");

	assert(check.itWasCalled === true);
});