var Test = module.exports = require("./Test");
var assert = Test.assert;
Test.assertClassInstance = function(Class, instance){
	assert(instance instanceof Class);
	assert(instance.constructor === Class);
	assert(Object.getPrototypeOf(instance) === Class.prototype);
};

Test.assertBaseExt = function(Base, Ext){
	assert(Object.getPrototypeOf(Ext.prototype) === Base.prototype);
};

Test.assertClass = function(Class){
	Test.assertClassInstance(Class, new Class());
	Test.assertClassInstance(Class, Class());

	var Class2 = Class.extend();
	var Class3 = Class2.extend();
	var Class4 = Class3.extend();

	Test.assertBaseExt(Class, Class2);
	Test.assertBaseExt(Class2, Class3);
	Test.assertBaseExt(Class3, Class4);
};