test("one", function(){

	this.view.addClass("wires");

	View.h2("Wires");

	View(function(){
		this.addClass("two cols");

		View("left");
		View("right");

	});

	View(function(){
		this.addClass("two cols");

		$(window).resize(function(){
			if (this.$el.width() < 400){
				console.log("outer break");
				this.addClass("break");
				
			} else {
				console.log("outer stack");
				this.removeClass("break");
			}
		}.bind(this));

		View("left").filler("2s")
		View(function(){

			this.addClass("two cols");

			$(window).resize(function(){
				if (this.$el.width() < 400)
					this.addClass("break");
				else
					this.removeClass("break");
			}.bind(this));

			View("inner left").filler("2s");
			View("inner right").filler("2s");

		});

		setTimeout(function(){
			$(window).resize();
			
		}, 0);
	});

	View(function(){
		this.addClass("three cols");

		View("left");
		View("center");
		View("right");

	})

	View(function(){
		this.addClass("four cols");

		View("left");
		View("left center");
		View("right center");
		View("right");

	})
});

