var test = require("test42");
var view = require("view42");
var assert = test.assert;
var util = require("util42");
var is = util.is;

var View = require("./View1");
var Text = View.Text;
var Content = View.Content;

test("Class", function(){
	test.assertClass(View)
});

test("basic", function(){
	var view1 = View("hello world");
	view1.append({
		my: "str",
		div: View("div")
	});
	view1.my.textContent = "new string";
	// console.log(this);
	this.view.body.nested.$el.append(view1.el);
	this.view.body.show();
});

test("extend", function(){
	var V = View.extend(function(){
		this.append({
			my: "str",
			div: View("div")
		});
	});
	var view1 = V();
	view1.my.textContent = "new string";
	// console.log(this);
	this.view.body.nested.$el.append(view1.el);
	this.view.body.show();
});